mapper.select:
SELECT id, patient_id AS patientId, patient_name AS patientName, sex, birth_day AS birthDay, create_time AS createTime FROM patient
AND patient_id LIKE CONCAT('%', #{patientId}, '%')
AND patient_name LIKE CONCAT('%', #{patientName}, '%')
AND create_time >= #{dateStart}
AND create_time <= #{dateEnd}
ORDER BY create_time DESC
